log("Day 2 Part 2");

const input =
  `



`.split(
    "\n"
  );
// const input = `ULL
// RRDDD
// LURDL
// UUUUD`.split("\n");

const n = null;

const keypad = [
  [n, n, 1, n, n],
  [n, 2, 3, 4, n],
  [5, 6, 7, 8, 9],
  [n, "A", "B", "C", n],
  [n, n, "D", n, n],
];

const position = { y: 2, x: 0 };
let code = "";

// const nullCheck = () => keypad[position.y][position.x] !== n;
const postionCheck = () => keypad[position.y][position.x];

for (const direction of input) {
  for (const letter of direction) {
    let newY = position.y;
    let newX = position.x;
    if (letter === "U") {
      newY -= 1;
    }
    if (letter === "D") {
      newY += 1;
    }
    if (letter === "L") {
      newX -= 1;
    }
    if (letter === "R") {
      newX += 1;
    }
    if (
      newY >= 0 &&
      newY < 5 &&
      newX >= 0 &&
      newX < 5 &&
      keypad[newY][newX] !== n
    ) {
      position.y = newY;
      position.x = newX;
    }
  }
  code += postionCheck();
}

log(code);
